mapper.select:

select * from tibetdb.user
 where user_id=#{user_id}
 and user_pw=#{user_pw}


select * from tibetdb.user
 where user_name=#{user_name}
 and user_email=#{user_email}


select * from tibetdb.user
 where user_id=#{user_id}
 and user_name=#{user_name}
 and user_email=#{user_email}

select case count(*) when 1 then 'true' when 0 then 'false' end
 from tibetdb.user
 where user_id = #{id}
